    private void siloActionPerformed(java.awt.event.ActionEvent evt) {                                         
        this.setVisible(false);
        s = new VueSilo(this,true);
        initSilo();
        s.setVisible(true);
        this.setVisible(true);
        if(s.select!=-1){
            a.stopAlarme();
            jPanel3.setBackground(Color.green);
            majBD(s.select-1, s.action);
        }
        majAlarme();
    }
    
